import { withStyles} from '@material-ui/core/styles';
import { Button } from '@material-ui/core';

import { createTheme } from '@material-ui/core';
import { ThemeProvider } from '@material-ui/core';

import { grey } from '@material-ui/core/colors';
import { yellow } from '@material-ui/core/colors';
import { green } from '@material-ui/core/colors';
import { red } from '@material-ui/core/colors';

const WrongTheme = createTheme({ palette: { primary: red } });
const CorrectTheme = createTheme({ palette: { primary: green } });

const ButtonStyles = theme => ({
  buttonPadding: {    
    padding: '0px',
  },
  buttonMargin: {
      margin: '5px',
  }
});

const ButtonThemes = createTheme({
  palette: {
    primary: grey,
    warning: yellow,
    success: green,
    error: red
  },
});

function KeyboardButton( Props ) {
  const { classes } = Props;

  return (
    <div>
      <ThemeProvider theme={ Props.Theme }>
      <Button
        variant="contained"
        // color={ Props.Color }
        color="primary"
        className={ ( classes.buttonPadding, classes.buttonMargin ) }
      >
        { Props.Letter }
      </Button>
      </ThemeProvider>
    </div>

  );
}

export default withStyles( ButtonStyles )( KeyboardButton );
